import React from 'react';
import './App.css';



const goods = [
  {
    name: 'apple',
    price: '50',
    url: 'https://loremflickr.com/320/240/apple',
    remainder: '44'
  },
  {
    name: 'pineapple',
    price: '66',
    url: 'https://loremflickr.com/320/240/pineapple',
    remainder: '50'
  },
  {
    name: 'banana',
    price: '72',
    url: 'https://loremflickr.com/320/240/bananas',
    remainder: '25'
  },
  {
    name: 'tomato',
    price: '50',
    url: 'https://loremflickr.com/320/240/tomato',
    remainder: '44'
  },
  {
    name: 'peach',
    price: '112',
    url: 'https://loremflickr.com/320/240/peach',
    remainder: '12'
  }
];

const namesOfColumns = ['Name of product', 'Price of product', 'Picture', 'Balance in stock'];


function CreateTable(props) {
  return (
    <table>
      <thead>
        <tr>
          <th>List of goods</th>
        </tr>
        <tr>
          {props.namesOfColumns.map((item, index) => (
            <th key={index}>{item}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {props.goods.map((item, index) => {
          const arrData = [];
          for (let key in item) {
            arrData.push(
              <td className='cell' key={`${index}-${key}`}>
                {key === 'url' ? <img src = {item[key]} alt={item.name} width="100px" /> : item[key]}
                </td>
            );
          }
          return <tr key={index}>{arrData}</tr>;
        })}
      </tbody>
    </table>
  );
}

let tableOfGoods = <CreateTable namesOfColumns = {namesOfColumns} goods = {goods} />

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        Hello!
        {tableOfGoods}
        />
      </header>
    </div>
  );
}

export default App;
