import React from 'react';
import './App.css';


type TypeGoods = {
  name: string;
  price: number;
  url: string;
  remainder: number;
}

type TypeGoodsNames = {
  namesOfColumns: string[]
}

const goods: TypeGoods[] = [
  {
    name: 'apple',
    price: 50,
    url: 'https://loremflickr.com/320/240/apple',
    remainder: 44,
  },
  {
    name: 'pineapple',
    price: 66,
    url: 'https://loremflickr.com/320/240/pineapple',
    remainder: 50,
  },
  {
    name: 'banana',
    price: 72,
    url: 'https://images.unsplash.com/photo-1543218024-57a70143c369?q=80&w=1935&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D',
    remainder: 25,
  },
  {
    name: 'tomato',
    price: 50,
    url: 'https://loremflickr.com/320/240/tomato',
    remainder: 35,
  },
  {
    name: 'peach',
    price: 112,
    url: 'https://loremflickr.com/320/240/peach',
    remainder: 12,
  }
];

const namesOfColumns: TypeGoodsNames = ['Name of product', 'Price of product', 'Picture', 'Balance in stock'];


function CreateTable(props: TypeGoods) {
  return (
    <table>
      <thead>
        <tr>
          <th>List of goods</th>
        </tr>
        <tr>
          {props.namesOfColumns.map((item, index) => (
            <th key={index}>{item}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {props.goods.map((item, index) => {
          const arrData = [];
          for (let key in item) {
            arrData.push(
              <td className='cell' key={`${index}-${key}`}>
                {key === 'url' ? <img src = {item[key]} alt={item.name} width="100px" /> : item[key]}
                </td>
            );
          }
          return <tr key={index}>{arrData}</tr>;
        })}
      </tbody>
    </table>
  );
}

let tableOfGoods = <CreateTable namesOfColumns = {namesOfColumns} goods = {goods} />

function App() {
  return (
    <div className="App">
      <header className="App-header">
        {tableOfGoods}
      </header>
    </div>
  );
}

export default App;
