import { useEffect } from "react"
import { useState } from "react"

export default function InputWithSave() {
const [message, setMessage] = useState('')
const [savedMessage, setSavedMessage] = useState('')


const LOCAL_STORAGE_KEY = 'ita-key'

function load() {
    const isMessage = localStorage.getItem('ita-key')
    if(isMessage) {
        setSavedMessage(isMessage)
    }

}

useEffect(() => {
    load()
}, [])

    return <>
    <input onChange={(e) => setMessage(e.target.value)} />
    <button onClick={() => localStorage.setItem(LOCAL_STORAGE_KEY, message)}> Save </button>
    <p>Saved: {savedMessage} </p>
    <button onClick={load}> Refresh </button>
    </>
}
