import { useState } from 'react';
import '../createTable.css';
import { CreateDates, getDates } from './CreateDates';


type CellType = {
    id: number;
    key: number;
    marked: boolean;
}

export default function CreateCell(props: CellType) {
    const [mark, setMark] = useState('')
    function handleMark() {
        setMark((prev) => {
            return prev === '' ? 'присутствует' : '';
    })
}

    return (
        <div className='cell' key={props.id} onClick={handleMark} style ={{color:'green'}}>{mark}</div>
    )
}
