import { useState } from 'react';
import '../createTable.css';


type CellType = {
    id: number;
    student: string;
    mark: boolean;
    toggleMark: (student: string, id: number) => void;
};

export const CreateCell: React.FC<CellType> = ({ id, student, mark, toggleMark }) => {
    return (
        <div
            className='cell'
            onClick={() => toggleMark(student, id)}
            style={{color: mark ? 'green' : ''}}>
            {mark ? 'присутствует' : ''}
        </div>
    );
}
