import '../createTable.css';
import { useState, useEffect } from 'react';

export default function CreateTable() {
    let getDates = (() => {
        const datesArray = ['names / dates']
        let today = new Date()
        for (let i = 0; i < 5; i++) {
            let newDay = new Date(today)
            newDay.setDate(today.getDate() + i)
            const year = newDay.getFullYear();
            const month = (newDay.getMonth() + 1).toString().padStart(2, '0');
            const day = newDay.getDate().toString().padStart(2, '0');
            datesArray.push(`${day}/${month}/${year}`)
        }
        return datesArray;
    })()


    type StudentsList = string[]
    const LOCAL_STORAGE_KEY = 'PASS'
    const [students, setStudents] = useState<StudentsList>(() => {
        const storedStudents = localStorage.getItem(LOCAL_STORAGE_KEY);
        return storedStudents ? JSON.parse(storedStudents) : []
    })
    const [studentsList, setStudentsList] = useState<StudentsList>([]);
    

    function addStudent() {
        const countCells: number = 5
        const studentName = prompt('Enter student name');
        if (studentName) {
            setStudents(prev => [...prev, studentName])
        } else {
            return null
        }
        // return (
        // <div className='row'>
        //     {/* <div className='cell'>{students.map((item) => {
        //         return item;
        //     })}</div>
        //     {Array.from({length: countCells}).map((_, index) => {
        //         return <CreateCell key = {index}/>
        //     })} */}
        // </div>
        // )
    }

    function showStudent() {
        const data = localStorage.getItem(LOCAL_STORAGE_KEY)
        if (data) {
            const parsedData = JSON.parse(data)
            setStudentsList(parsedData)
        }
    }


    useEffect(() => {
        localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(students))
        showStudent()
    }, [students])

    // useEffect(() => {
    //     const storedStudents = localStorage.getItem(LOCAL_STORAGE_KEY)
    //     if (storedStudents) {
    //         setStudentsList(JSON.parse(storedStudents))
    //     }
    // }, [])


    return (
        <>
            <div className="wrapper flex">
                <div className='row'>
                    {getDates.map((item, index) => {
                        return (<div className='cell' key={index}>{item}</div>)
                    })}
                </div>


                <div style ={{
                    padding:'20px'
                }}>
                    <button className='btn' onClick={addStudent}>Add student</button>
                    <button className='btn'>Add date</button>
                    <p>{studentsList}</p>
                </div>
            </div>
        </>
    )
}
