import { useState } from 'react';
import CreateCell from './CreateCell';
export default function AddStudent() {
    const LOCAL_STORAGE_KEY = 'PASS'
    const [studentData, setStudentData] = useState('')

    const countCells: number = 5
    const studentName = prompt('Enter student name');
    const students: string[] = []
    localStorage.setItem(LOCAL_STORAGE_KEY, studentName)
    return (
    <div className='row'>
        <div className='cell'>{students.map((item) => {
            return item;
        })}</div>
        {Array.from({length: countCells}).map((_, index) => {
            return <CreateCell key = {index}/>
        })}
    </div>
    )
}
