import { useState } from "react"


export default function Canvas() {
    const [coord, setCoord] = useState({x: NaN, y: NaN})
  
    function tracking(x: number, y:number) {
      setCoord({x, y})
    }
  
let inBound = !isNaN(coord.x) ? true : false


    return <><div
    style = {{
        width:'200px',
        height:'200px',
        backgroundColor: inBound ? 'gold' : 'red',
        margin:'20px',
    }}
    onMouseEnter={(e) => tracking(e.clientX, e.clientY)}
    onMouseLeave={() => console.log({x:NaN, y:NaN})}
    onMouseMove={(e) => tracking(e.clientX, e.clientY)}
    >
    </div>
    <div>X: {coord.x}</div>
    <div>Y: {coord.y}</div>
    </>
  
  }
