import { useState } from "react"




export default function useMyHook() {
    const [coord, setCoord] = useState({x: NaN, y: NaN})
  
    function tracking(x: number, y:number) {
      setCoord({x, y})
    }
    let inBound = !isNaN(coord.x) && !isNaN(coord.y)
return {
    coord,
    tracking,
    inBound,
}
}



export default function Canvas() {
    let myHook = useMyHook()

let color: string;

if(myHook.inBound) {
    if(myHook.coord.x + myHook.coord.y > 650) {
        color = 'blue'
    } else {
        color = 'red'
    }
} else {
    color = 'yellow'
}


    return <><div
    style = {{
        width:'200px',
        height:'200px',
        backgroundColor: color,
        margin:'20px',
        transition: '0.5s'
    }}
    onMouseEnter={(e) => myHook.tracking(e.clientX, e.clientY)}
    onMouseLeave={() => myHook.setCoord({x:NaN, y:NaN})}
    onMouseMove={(e) => myHook.tracking(e.clientX, e.clientY)}
    >
    </div>
    <div>X: {myHook.coord.x}</div>
    <div>Y: {myHook.coord.y}</div>
    </>
    
  
  }
