export default function Canvas() {
    const [coord, setCoord] = useState({x: NaN, y: NaN})
  
    function tracking(x: number, y:number) {
      setCoord({x, y})
    }
  
    return <><div
    style = {{
        width:'200px',
        height:'200px',
        backgroundColor: 'gold',
        margin:'20px',
    }}
    onMouseEnter={(e) => setCoord({x:e.clientX, y:e.clientY}) }
    onMouseLeave={() => console.log({x:NaN, y:NaN})}
    onMouseMove={(e) => setCoord({x:e.clientX, y:e.clientY})}
    >
    </div>
    <div>X: {coord.x}</div>
    <div>Y: {coord.y}</div>
    </>
  
  }
