import useMyHook from "./UseMyHook";


export default function Canvas() {
    let myHook = useMyHook()

let color: string;

if(myHook.inBound) {
    if(myHook.x + myHook.y > 650) {
        color = 'blue'
    } else {
        color = 'red'
    }
} else {
    color = 'yellow'
}


    return <><div
    style = {{
        width:'200px',
        height:'200px',
        backgroundColor: color,
        margin:'20px',
        transition: '0.5s'
    }}
    onMouseEnter={(e) => myHook.tracking(e.clientX, e.clientY)}
    onMouseLeave={() => myHook.tracking(NaN, NaN)}
    onMouseMove={(e) => myHook.tracking(e.clientX, e.clientY)}
    >
    </div>
    <div>X: {myHook.x}</div>
    <div>Y: {myHook.y}</div>
    </>
    
  
  }
